import React, { Component } from 'react';

class content extends Component {
    render() {
        return (
    <div>
    <hr class="mb-5"/>
<p class="font-weight-bold">Example</p>
<p>Add <code>d-flex align-items-center justify-content-center</code> classes to the parent element to center its
  content vertically and horizontally.</p>
<div class="border border-light p-3 mb-4">

  </div>

</div>
           
        );
    }
}

export default content;
