import React, { Component } from 'react';

class content extends Component {


    
    

    render() {
       const  movies = [
            { title: "I Care a Lot" , img : "https://occ-0-1001-448.1.nflxso.net/dnm/api/v6/6AYY37jfdO6hpXcMjf9Yu5cnmO0/AAAABTnDrXenR7tkDPdtvmVbm44VoVh2WZmrDogXjgNVd9B9cIrrj3D-pQPVO-tj2EhRTwCWztGtzM-pPdeP1FlTH9qS7gLI.jpg?r=ccb"},
            { title: "Paper Lives " , img : "https://resizing.flixster.com/B5GMmCNfMRbmhOA9D-yUuQhOrv8=/ems.ZW1zLXByZC1hc3NldHMvbW92aWVzLzczNWUyY2MxLTEyMmMtNGViZS1iZTc0LTBjMGM4YzQyOWZiMS5qcGc="},
            { title: "movie1" , img : "https://i.ytimg.com/vi/EoGojQE4p-8/maxresdefault.jpg"} 
                 ]
        return (
    <div>
    <hr class="mb-5"/>

<div class="border border-light p-3 mb-4">

<table class="table table-dark">

  <tbody>
    
      {movies.map((m,i)=>{
  return(
    <tr >
    <div class="card">
  <div class="card-body">
    <p class="card-text text-secondary">{m.title}</p>
  </div>
  <img src= {m.img}  alt="" style={{width: "120px", height:"120px" }} /> 
  <div class="card-body">
  </div>
</div>  
  </tr>

  )
})
}
    
  </tbody>
</table>


  </div>

</div>
           
        );
    }
}

export default content;
